import { CIPipelineFactory } from './src/ci';

async function runAutomatedTests() {
  const ci = CIPipelineFactory.fromEnvironment();
  await ci.connect();

  // Trigger test pipeline
  const run = await ci.triggerPipeline({
    workflow: 'ci.yml',
    branch: 'main',
  });

  // Wait for completion
  let status = await ci.getPipelineStatus(run.id);
  while (status === 'running' || status === 'pending') {
    await new Promise(resolve => setTimeout(resolve, 5000));
    status = await ci.getPipelineStatus(run.id);
  }

  // Get results
  if (status === 'success') {
    console.log('✅ All tests passed');
    const logs = await ci.getPipelineLogs(run.id);
    console.log(logs);
  } else {
    console.log('❌ Tests failed');
    const logs = await ci.getPipelineLogs(run.id);
    console.error(logs);
    process.exit(1);
  }
}

import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { CallToolRequestSchema, ErrorCode, ListToolsRequestSchema, McpError } from '@modelcontextprotocol/sdk/types.js';

class MyMCPServer {
  private server: Server;

  constructor() {
    this.server = new Server({
      name: 'my-mcp-server',
      version: '1.0.0',
    }, {
      capabilities: { tools: {} },
    });
    this.setupToolHandlers();
  }

  private setupToolHandlers(): void {
    // List tools handler
    this.server.setRequestHandler(ListToolsRequestSchema, async () => {
      return {
        tools: [
          {
            name: 'my_tool',
            description: 'Tool description',
            inputSchema: {
              type: 'object',
              properties: {
                param: { type: 'string', description: 'Parameter description' }
              },
              required: ['param']
            }
          }
        ]
      };
    });

    // Call tool handler
    this.server.setRequestHandler(CallToolRequestSchema, async (request) => {
      const { name, arguments: args } = request.params;
      if (!args) {
        throw new McpError(ErrorCode.InvalidParams, "Arguments are required");
      }

      try {
        switch (name) {
          case 'my_tool':
            return { content: [{ type: 'text', text: 'Result' }] };
          default:
            throw new McpError(ErrorCode.MethodNotFound, `Unknown tool: ${name}`);
        }
      } catch (error) {
        if (error instanceof Error) {
          throw new McpError(ErrorCode.InternalError, error.message);
        }
        throw new McpError(ErrorCode.InternalError, "An unknown error occurred");
      }
    });
  }

  public async run(): Promise<void> {
    const transport = new StdioServerTransport();
    await this.server.connect(transport);
    console.error('MCP server running on stdio');
  }
}

// Run the server
const server = new MyMCPServer();
server.run().catch(console.error);

