// utils/converter.ts
export function convertJSONLToCanvasL(
  jsonlPath: string,
  canvaslPath: string,
  options?: {
    version?: string;
    schema?: string;
    r5rsEngine?: string;
  }
): void {
  const automaton = new AdvancedSelfReferencingAutomaton(jsonlPath);

  // Set CanvasL format
  automaton.fileFormat = 'canvasl';

  // Set directives
  automaton.directives = {
    version: options?.version || '1.0',
    schema: options?.schema || 'canvasl-v1',
    'r5rs-engine': options?.r5rsEngine || 'r5rs-canvas-engine.scm'
  };

  // Save as CanvasL
  automaton.filePath = canvaslPath;
  automaton.save();
}

function detectFormat(filePath: string): 'jsonl' | 'canvasl' {
  if (filePath.endsWith('.canvasl')) {
    return 'canvasl';
  }
  if (filePath.endsWith('.jsonl')) {
    return 'jsonl';
  }
  // Default to jsonl for backward compatibility
  return 'jsonl';
}
function detectFormat(filePath: string): 'jsonl' | 'canvasl' {
  if (filePath.endsWith('.canvasl')) return 'canvasl';
  if (filePath.endsWith('.jsonl')) return 'jsonl';
  // Default to jsonl for backward compatibility
  return 'jsonl';
}

function parseAutomatonFile(filePath: string): AutomatonObjects {
  const format = detectFormat(filePath);
  const content = readFileSync(filePath, 'utf-8');

  if (format === 'canvasl') {
    return parseCanvasL(content);
  } else {
    return parseJSONL(content);
  }
}

function parseCanvasL(content: string): ParsedCanvasL {
  const directives: Record<string, string> = {};
  const objects: CanvasObject[] = [];

  const lines = content.split('\n');
  for (const line of lines) {
    // Parse directives
    if (line.startsWith('@')) {
      const [key, value] = line.split(':').map(s => s.trim());
      directives[key.substring(1)] = value;
      continue;
    }

    // Parse JSONL objects
    if (line.trim().startsWith('{')) {
      const obj = JSON.parse(line);
      objects.push(obj);
    }
  }

  return { directives, objects };
}

